import React, { useState } from 'react';

const UploadTab = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [analysis, setAnalysis] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
      setError(null);
    } else {
      setError('Please select a PDF file');
      setFile(null);
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/upload-resume', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setAnalysis(result);
    } catch (err) {
      setError('Error uploading file: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const renderAnalysis = () => {
    if (!analysis) return null;

    return (
      <div className="analysis-results">
        <h2>Resume Analysis Results</h2>
        
        <div className="personal-info">
          <h3>Personal Information</h3>
          <p><strong>Name:</strong> {analysis.name}</p>
          <p><strong>Email:</strong> {analysis.email}</p>
          <p><strong>Phone:</strong> {analysis.phone}</p>
        </div>

        <div className="skills-section">
          <div className="core-skills">
            <h3>Core Skills</h3>
            <div className="skills-list">
              {analysis.core_skills.map((skill, index) => (
                <span key={index} className="skill-tag">{skill}</span>
              ))}
            </div>
          </div>

          <div className="soft-skills">
            <h3>Soft Skills</h3>
            <div className="skills-list">
              {analysis.soft_skills.map((skill, index) => (
                <span key={index} className="skill-tag soft">{skill}</span>
              ))}
            </div>
          </div>
        </div>

        <div className="experience-section">
          <h3>Work Experience</h3>
          {analysis.work_experience.map((job, index) => (
            <div key={index} className="job-item">
              <h4>{job.position} at {job.company}</h4>
              <p className="duration">{job.duration}</p>
              <p>{job.description}</p>
            </div>
          ))}
        </div>

        <div className="education-section">
          <h3>Education</h3>
          {analysis.education.map((edu, index) => (
            <div key={index} className="education-item">
              <h4>{edu.degree}</h4>
              <p>{edu.institution} - {edu.year}</p>
            </div>
          ))}
        </div>

        <div className="rating-section">
          <h3>Resume Rating</h3>
          <div className="rating">
            <span className="rating-score">{analysis.resume_rating}/10</span>
            <div className="rating-bar">
              <div 
                className="rating-fill" 
                style={{width: `${analysis.resume_rating * 10}%`}}
              ></div>
            </div>
          </div>
        </div>

        <div className="suggestions-section">
          <div className="improvement-areas">
            <h3>Areas for Improvement</h3>
            <p>{analysis.improvement_areas}</p>
          </div>

          <div className="upskill-suggestions">
            <h3>Upskilling Suggestions</h3>
            <p>{analysis.upskill_suggestions}</p>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="upload-tab">
      <div className="upload-section">
        <h2>Upload Your Resume</h2>
        <div className="file-upload">
          <input
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            className="file-input"
          />
          {file && (
            <p className="file-selected">
              Selected: {file.name}
            </p>
          )}
          <button
            onClick={handleUpload}
            disabled={!file || loading}
            className="upload-button"
          >
            {loading ? 'Analyzing...' : 'Upload & Analyze'}
          </button>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
      </div>

      {renderAnalysis()}
    </div>
  );
};

export default UploadTab;
